function descriptions_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('descriptions');
	if(!$arrPerm[1]){
		return false;
	}

	$data['description'] = br2nl(makeSafe($_REQUEST['description']));
	$data['infection'] = makeSafe($_REQUEST['infection']);
		if($data['infection'] == empty_lookup_value){ $data['infection'] = ''; }
	$data['symptom'] = makeSafe($_REQUEST['symptom']);
		if($data['symptom'] == empty_lookup_value){ $data['symptom'] = ''; }
	$data['category'] = makeSafe($_REQUEST['category']);
		if($data['category'] == empty_lookup_value){ $data['category'] = ''; }
	$data['date'] = parseCode('<%%creationDate%%>', true, true);
	if($data['description']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Description': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	if($data['infection']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Infection': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	if($data['symptom']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Symptom': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	if($data['category']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Category': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	// hook: descriptions_before_insert
	if(function_exists('descriptions_before_insert')){
		$args=array();
		if(!descriptions_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `descriptions` set       `description`=' . (($data['description'] !== '' && $data['description'] !== NULL) ? "'{$data['description']}'" : 'NULL') . ', `infection`=' . (($data['infection'] !== '' && $data['infection'] !== NULL) ? "'{$data['infection']}'" : 'NULL') . ', `symptom`=' . (($data['symptom'] !== '' && $data['symptom'] !== NULL) ? "'{$data['symptom']}'" : 'NULL') . ', `category`=' . (($data['category'] !== '' && $data['category'] !== NULL) ? "'{$data['category']}'" : 'NULL') . ', `date`=' . "'{$data['date']}'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"descriptions_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: descriptions_after_insert
	if(function_exists('descriptions_after_insert')){
		$res = sql("select * from `descriptions` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!descriptions_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('descriptions', $recID, getLoggedMemberID());

	return $recID;
}

function descriptions_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('descriptions');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='descriptions' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='descriptions' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: descriptions_before_delete
	if(function_exists('descriptions_before_delete')){
		$args=array();
		if(!descriptions_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	sql("delete from `descriptions` where `id`='$selected_id'", $eo);

	// hook: descriptions_after_delete
	if(function_exists('descriptions_after_delete')){
		$args=array();
		descriptions_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='descriptions' and pkValue='$selected_id'", $eo);
}

